import { configureStore } from "@reduxjs/toolkit";
import themeReducer from "./themeSlice";
import graphReducer from "./graphSlice"; // ✅ import

const store = configureStore({
  reducer: {
    theme: themeReducer,
    graph: graphReducer, // ✅ register
  },
});

export default store;
