import React, { useRef, useEffect } from "react";
import * as d3 from "d3";

const Graph = ({ data }) => {
  const svgRef = useRef();

  useEffect(() => {
    if (!data) return;

    const width = 1200;
    const height = 600;
    const centerX = width / 2;
    const centerY = height / 2;

    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    svg
      .attr("width", width)
      .attr("height", height)
      .attr("viewBox", [0, 0, width, height])
      .style("background", "black");

    // Add defs for arrowhead
    const defs = svg.append("defs");
    defs
      .append("marker")
      .attr("id", "arrowhead")
      .attr("viewBox", "0 -5 10 10")
      .attr("refX", 15)
      .attr("refY", 0)
      .attr("markerWidth", 6)
      .attr("markerHeight", 6)
      .attr("orient", "auto")
      .append("path")
      .attr("d", "M0,-5L10,0L0,5")
      .attr("fill", "cyan");

    // Zoom group
    const g = svg.append("g").attr("class", "zoom-group");

    svg.call(
      d3.zoom().on("zoom", (event) => {
        g.attr("transform", event.transform);
      })
    );

    const { nodes, links } = data;

    // Layout positions
    const centerNode = nodes.find((n) => n.type === "center");
    const inflowNodes = nodes.filter((n) => n.type === "inflow");
    const outflowNodes = nodes.filter((n) => n.type === "outflow");

    centerNode.x = centerX;
    centerNode.y = centerY;

    inflowNodes.forEach((node, i) => {
      node.x = centerX - 300;
      node.y = centerY - (inflowNodes.length - 1) * 60 / 2 + i * 60;
    });

    outflowNodes.forEach((node, i) => {
      node.x = centerX + 300;
      node.y = centerY - (outflowNodes.length - 1) * 60 / 2 + i * 60;
    });

    const allNodes = [...inflowNodes, centerNode, ...outflowNodes];

    // Draw links
    g.selectAll("line.link")
      .data(links)
      .join("line")
      .attr("x1", (d) => getNode(d.source).x)
      .attr("y1", (d) => getNode(d.source).y)
      .attr("x2", (d) => getNode(d.target).x)
      .attr("y2", (d) => getNode(d.target).y)
      .attr("stroke", (d) => (d.type === "inflow" ? "magenta" : "cyan"))
      .attr("stroke-width", 2)
      .attr("stroke-dasharray", "4,2")
      .attr("marker-end", "url(#arrowhead)");

    // Draw link labels
    g.selectAll("text.link-label")
      .data(links)
      .join("text")
      .attr("x", (d) => (getNode(d.source).x + getNode(d.target).x) / 2)
      .attr("y", (d) => (getNode(d.source).y + getNode(d.target).y) / 2 - 5)
      .attr("text-anchor", "middle")
      .attr("fill", "white")
      .style("font-size", "12px")
      .text((d) => `${d.amount} BTC | ${d.time}`);

    // Draw nodes
    g.selectAll("rect.node")
      .data(allNodes)
      .join("rect")
      .attr("x", (d) => d.x - 60)
      .attr("y", (d) => d.y - 20)
      .attr("rx", 15)
      .attr("ry", 15)
      .attr("width", 120)
      .attr("height", 40)
      .attr("fill", (d) =>
        d.label === "Changenow" ? "orange" : "#2bb3b3"
      )
      .attr("stroke", "white")
      .attr("stroke-width", 1.5)
      .attr("filter", "drop-shadow(0 0 5px black)");

    // Draw node labels
    g.selectAll("text.node-label")
      .data(allNodes)
      .join("text")
      .attr("x", (d) => d.x)
      .attr("y", (d) => d.y + 5)
      .attr("text-anchor", "middle")
      .attr("fill", "white")
      .style("font-size", "14px")
      .text((d) => d.label);

    function getNode(id) {
      return nodes.find((n) => n.id === id);
    }
  }, [data]);

  // SVG Export logic
  const downloadSVG = () => {
    const svg = svgRef.current;
    if (!svg) return;

    const clone = svg.cloneNode(true);
    inlineAllStyles(svg, clone);

    const serializer = new XMLSerializer();
    const svgString = serializer.serializeToString(clone);
    const blob = new Blob([svgString], { type: "image/svg+xml;charset=utf-8" });
    const url = URL.createObjectURL(blob);

    const link = document.createElement("a");
    link.href = url;
    link.download = "graph-export.svg";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const inlineAllStyles = (original, clone) => {
    const originalEls = original.querySelectorAll("*");
    const cloneEls = clone.querySelectorAll("*");

    for (let i = 0; i < originalEls.length; i++) {
      const style = window.getComputedStyle(originalEls[i]);
      for (const key of style) {
        cloneEls[i].style[key] = style.getPropertyValue(key);
      }
    }

    clone.setAttribute("width", original.getAttribute("width"));
    clone.setAttribute("height", original.getAttribute("height"));
    const vb = original.getAttribute("viewBox");
    if (vb) clone.setAttribute("viewBox", vb);
  };

  return (
    <>
      <svg ref={svgRef} />
      <button
        onClick={downloadSVG}
        style={{ marginTop: "1rem", padding: "8px 12px" }}
      >
        Export SVG
      </button>
    </>
  );
};

export default Graph;
