import { createSlice } from '@reduxjs/toolkit';

export const graphSlice = createSlice({
  name: 'graph',
  initialState: {
    nodePositions: {},
  },
  reducers: {
    setNodePositions: (state, action) => {
      state.nodePositions = {
        ...state.nodePositions,
        ...action.payload,
      };
    },
  },
});

export const { setNodePositions } = graphSlice.actions;
export default graphSlice.reducer;
