import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const Graph = ({ nodes, links }) => {
  const svgRef = useRef();
  const gRef = useRef(); // store g for zoom transform reference

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    const width = 1200;
    const height = 800;

    const inflowLinks = links.filter(
      link => link.target === nodes[0]?.id || link.target?.id === nodes[0]?.id
    );
    const outflowLinks = links.filter(
      link => link.source === nodes[0]?.id || link.source?.id === nodes[0]?.id
    );
    const centerNode = nodes[0];

    const directedLinks = links.map(link => {
      const sourceId = typeof link.source === "string" ? link.source : link.source?.id;
      const targetId = typeof link.target === "string" ? link.target : link.target?.id;
      if (sourceId === centerNode?.id) {
        return { ...link, source: sourceId, target: targetId };
      } else if (targetId === centerNode?.id) {
        return { ...link, source: centerNode.id, target: sourceId };
      }
      return { ...link };
    });

    const zoom = d3.zoom().scaleExtent([0.1, 10]).on("zoom", (event) => {
      g.attr("transform", event.transform);
    });

    svg.call(zoom);
    const g = svg.append("g");
    gRef.current = g; // store reference to apply transform on export

    g.append("defs").append("marker")
      .attr("id", "arrow")
      .attr("viewBox", "0 -5 10 10")
      .attr("refX", 28)
      .attr("refY", 0)
      .attr("markerWidth", 6)
      .attr("markerHeight", 6)
      .attr("orient", "auto")
      .append("path")
      .attr("d", "M0,-5L10,0L0,5")
      .attr("fill", "#00f2ff");

    const link = g.append("g")
      .selectAll("line")
      .data(directedLinks)
      .enter()
      .append("line")
      .attr("stroke-width", 2.5)
      .attr("stroke", d => parseFloat(d.amount) > 1 ? "#00f2ff" : "#ff00a1")
      .attr("stroke-dasharray", d => parseFloat(d.amount) > 1 ? "5,3" : "4,3")
      .attr("marker-end", "url(#arrow)");

    const simulation = d3.forceSimulation(nodes)
      .force("link", d3.forceLink(directedLinks).id(d => d.id).distance(200))
      .force("charge", d3.forceManyBody().strength(-400))
      .force("center", d3.forceCenter(width / 2, height / 2));

    const node = g.append("g")
      .selectAll("foreignObject")
      .data(nodes)
      .enter()
      .append("foreignObject")
      .attr("width", 180)
      .attr("height", 60)
      .attr("x", -90)
      .attr("y", -30)
      .html(d => {
        const label = d.label || `${d.id.slice(0, 6)}...${d.id.slice(-6)}`;
        const dateTime = d.timestamp
          ? `<div style="font-size:10px; color:#eee; margin-top:2px;">${d.timestamp}</div>`
          : '';
        return `
          <div xmlns="http://www.w3.org/1999/xhtml" style="
            background: ${d.label === "Changenow" ? "#ff8800" : d.label === "Whitebit" ? "#b794f4" : d.label ? "#ffb6c1" : "#38b2ac"};
            border-radius: 25px;
            padding: 6px 10px;
            color: white;
            text-align: center;
            font-size: 13px;
            font-weight: 500;
            box-shadow: 0 0 8px rgba(255,255,255,0.5);
            cursor: move;
          ">
            <div>${label}</div>
            ${dateTime}
          </div>
        `;
      })
      .call(drag(simulation));

    const exportNodes = g.append("g")
      .attr("class", "svg-export-texts")
      .selectAll("text")
      .data(nodes)
      .enter()
      .append("text")
      .attr("text-anchor", "middle")
      .attr("fill", "white")
      .attr("font-size", 13)
      .attr("font-family", "sans-serif")
      .style("pointer-events", "none")
      .style("display", "none")
      .text(d => d.label || `${d.id.slice(0, 6)}...${d.id.slice(-6)}`);

    const edgeLabels = g.append("g")
      .selectAll("text")
      .data(directedLinks)
      .enter()
      .append("text")
      .attr("fill", "#fff")
      .attr("font-size", "12px")
      .attr("font-weight", "bold")
      .attr("text-anchor", "middle")
      .style("opacity", 1)
      .text(d => {
        const amount = parseFloat(d.amount?.replace(" BTC", ""));
        const timestamp = d.timestamp || "";
        return `${amount} BTC | ${timestamp}`;
      });

    simulation.on("tick", () => {
      const spacingX = 350;
      const spacingY = 200;
      const centerX = width / 2;
      const centerY = height / 2;

      if (centerNode && centerNode.fx === undefined) {
        centerNode.fx = centerX;
        centerNode.fy = centerY;
      }

      inflowLinks.forEach((link, i) => {
        const yOffset = inflowLinks.length === 1
          ? -350
          : (i - (inflowLinks.length - 1) / 2) * spacingY;

        const sourceNode = typeof link.source === "string"
          ? nodes.find(n => n.id === link.source)
          : link.source;

        if (sourceNode && sourceNode.fx === undefined) {
          sourceNode.fx = centerX - spacingX;
          sourceNode.fy = centerY + yOffset;
        }
      });

      outflowLinks.forEach((link, i) => {
        const yOffset = outflowLinks.length === 1
          ? 150
          : (i - (outflowLinks.length - 1) / 2) * spacingY;

        const targetNode = typeof link.target === "string"
          ? nodes.find(n => n.id === link.target)
          : link.target;

        if (targetNode && targetNode.fx === undefined) {
          targetNode.fx = centerX + spacingX;
          targetNode.fy = centerY + yOffset;
        }
      });

      link
        .attr("x1", d => d.source.x)
        .attr("y1", d => d.source.y)
        .attr("x2", d => d.target.x)
        .attr("y2", d => d.target.y);

      node
        .attr("x", d => d.x - 90)
        .attr("y", d => d.y - 30);

      edgeLabels
        .attr("x", d => (d.source.x + d.target.x) / 2)
        .attr("y", d => (d.source.y + d.target.y) / 2 - 20);

      exportNodes
        .attr("x", d => d.x)
        .attr("y", d => d.y + 5);
    });

    function drag(simulation) {
      return d3.drag()
        .on("start", event => {
          if (!event.active) simulation.alphaTarget(0.3).restart();
          event.subject.fx = event.subject.x;
          event.subject.fy = event.subject.y;
        })
        .on("drag", event => {
          event.subject.fx = event.x;
          event.subject.fy = event.y;
        })
        .on("end", event => {
          if (!event.active) simulation.alphaTarget(0);
        });
    }

    // Export logic
    const handleDownloadSVG = () => {
      const svgElement = svgRef.current;
      const clone = svgElement.cloneNode(true);
      const transform = d3.zoomTransform(svgElement);

      // Clone g to apply current transform
      const originalG = gRef.current.node();
      const clonedG = clone.querySelector("g");
      if (clonedG) {
        clonedG.setAttribute("transform", `translate(${transform.x},${transform.y}) scale(${transform.k})`);
      }

      // Remove foreignObject
      const foreignObjects = clone.querySelectorAll("foreignObject");
      foreignObjects.forEach(el => el.remove());

      // Show export labels
      const exportTextGroup = clone.querySelector(".svg-export-texts");
      if (exportTextGroup) {
        exportTextGroup.querySelectorAll("text").forEach(t => {
          t.style.display = "block";
        });
      }

      const serializer = new XMLSerializer();
      const source = serializer.serializeToString(clone);
      const blob = new Blob([source], { type: "image/svg+xml;charset=utf-8" });
      const url = URL.createObjectURL(blob);

      const link = document.createElement("a");
      link.href = url;
      link.download = "bitcoin-graph.svg";
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    };

    window.addEventListener("download-svg", handleDownloadSVG);

    return () => {
      window.removeEventListener("download-svg", handleDownloadSVG);
    };
  }, [nodes, links]);

  return (
    <svg
      ref={svgRef}
      width={1200}
      height={800}
      style={{
        backgroundColor: "#000",
        backgroundImage: "url('/graph-bg.png')",
        backgroundSize: "contain",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        cursor: "grab"
      }}
    />
  );
};

export default Graph;
