import React, { useState } from "react";
import { FaFilter, FaPlus, FaMinus } from "react-icons/fa";
import { AiOutlineWallet } from "react-icons/ai";

const Sidebar = ({ selectedAddress, data, onToggleNode, addedNodes }) => {
  const [activeTab, setActiveTab] = useState("outflows");
  const [addressInput, setAddressInput] = useState("");

  if (!selectedAddress || !data) return null;

  const renderList = (items, type) =>
    items.map((item, index) => {
      const isAdded = addedNodes.includes(item.address);
      return (
        <div
          key={`${type}-${index}`}
          className="flex justify-between items-center py-2 border-b border-gray-700"
        >
          <div className="text-sm text-white">
            <div className="font-semibold">{item.label || item.address}</div>
            <div className="text-gray-400 text-xs">
              {item.amount} BTC
            </div>
          </div>
          <button
            className={`rounded-full p-1.5 text-white ${
              isAdded ? "bg-red-600" : "bg-green-500"
            }`}
            onClick={() => onToggleNode(item.address, type)}
          >
            {isAdded ? <FaMinus /> : <FaPlus />}
          </button>
        </div>
      );
    });

  return (
    <div className="w-72 bg-[#1e1e1e] p-4 border-r border-gray-800 text-white h-screen overflow-y-auto">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-lg font-bold">TOGGLE VISIBLE TRACE NODES:</h2>
        <FaFilter className="text-yellow-400" />
      </div>

      <div className="flex items-center bg-[#2a2a2a] p-2 rounded mb-4">
        <AiOutlineWallet className="text-yellow-400 text-lg mr-2" />
        <input
          type="text"
          value={addressInput}
          onChange={(e) => setAddressInput(e.target.value)}
          placeholder="Enter wallet address"
          className="flex-1 bg-transparent text-white text-sm outline-none"
        />
        <button
          onClick={() => {
            if (addressInput) onToggleNode(addressInput, "custom");
            setAddressInput("");
          }}
          className="bg-yellow-400 text-black font-bold px-3 py-1 rounded ml-2"
        >
          ADD
        </button>
      </div>

      <div className="flex border-b border-gray-700 mb-2">
        {["inflows", "outflows"].map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`flex-1 py-2 text-sm font-semibold ${
              activeTab === tab
                ? "text-green-400 border-b-2 border-green-400"
                : "text-gray-400"
            }`}
          >
            {tab.toUpperCase()}
          </button>
        ))}
      </div>

      <div>
        {activeTab === "inflows"
          ? renderList(data.inflows, "inflow")
          : renderList(data.outflows, "outflow")}
      </div>
    </div>
  );
};

export default Sidebar;
