import React from "react";

const Sidebar = ({ selectedAddress, data, onToggleNode, addedNodes }) => {
  if (!selectedAddress || !data) return null;

  const renderList = (items, type) =>
    items.map((item, index) => {
      const isAdded = addedNodes.includes(item.address);
      return (
        <div
          key={`${type}-${index}`}
          className="flex items-start justify-between gap-2 p-3 mb-3 bg-white shadow rounded-lg border border-gray-200"
        >
          <div className="text-sm">
            <div className="font-semibold text-gray-800 break-all">{item.address}</div>
            <div className="text-gray-600">
              {item.amount} | {item.timestamp}
              {item.label && <> | {item.label}</>}
            </div>
          </div>
          <button
            onClick={() => onToggleNode(item.address, type)}
            className={`px-2 py-1 rounded text-white text-xs font-medium ${
              isAdded ? "bg-red-500 hover:bg-red-600" : "bg-green-500 hover:bg-green-600"
            }`}
          >
            {isAdded ? "−" : "+"}
          </button>
        </div>
      );
    });

  return (
    <div className="sidebar w-80 p-4 bg-gray-100 h-full overflow-y-auto border-r border-gray-300">
      <h3 className="text-lg font-bold text-gray-700 mb-3">Inflow</h3>
      {renderList(data.inflows, "inflow")}

      <h3 className="text-lg font-bold text-gray-700 mt-6 mb-3">Outflow</h3>
      {renderList(data.outflows, "outflow")}
    </div>
  );
};

export default Sidebar;
